    /**
     * 
     */
    private AtomicInteger reqCount = new AtomicInteger(0);

    public boolean limit() {
        long now = System.currentTimeMillis();
        if (now < time + timeout) {
            // 
            reqCount.addAndGet(1);
            return reqCount.get() <= limit;
        } else {
            // 
            time = now;
            reqCount = new AtomicInteger(0);
            return true;
        }
    }
}

