public class TokenBucket {
    /**
     * 
     */
    private long time;
    /**
     * 
     */
    private Double total;
    /**
     * token 
     */
    private Double rate;
    /**
     * 
     */
    private Double nowSize;

    public boolean limit() {
        long now = System.currentTimeMillis();
        nowSize = Math.min(total nowSize + (now - time) * rate);
        time = now;
        if (nowSize < 1) {
            // token
            return false;
        } else {
            // token
            nowSize -= 1;
            return true;
        }
    }

}
